mapper.update:
update account_book set 
ab_inout=#{ab_inout},ab_date=#{ab_date},ab_content=#{ab_content},ab_method=#{ab_method},ab_memo=#{ab_memo},ctno=#{ctno}, ab_amount=#{ab_amount}
where abno=#{abno};

mapper.sql:
from plan
where mno=#{mno} 
and p_month=#{p_month}

mapper.select:
select ct_top
from category
where ct_inout=1 and ct_lev=0 and ctno != 12;

select ct.ct_top ct_top, p_amount, pno
from plan p 
inner join category ct 
on p.ctno=ct.ctno
where mno=#{mno} 
and p_month=#{p_month}
group by ct.ct_top;

mapper.update:
update plan set 
p_amount=#{p_amount} 
where pno=#{pno}
